  status,
  onCancel,
  onReschedule
}: AppointmentCardProps) {
  const initials = serviceProviderName.split(' ').map(n => n[0]).join('').substring(0, 2).toUpperCase();
  const config = statusConfig[status];

  return (
    <Card className="p-4" data-testid={`card-appointment-${id}`}>
