import * as React from "react"
import { OTPInput, OTPInputContext } from "input-otp"
import { Dot } from "lucide-react"

import { cn } from "@/lib/utils"

const InputOTP = React.forwardRef<
  React.ElementRef<typeof OTPInput>,
  React.ComponentPropsWithoutRef<typeof OTPInput>
>(({ className, containerClassName, ...props }, ref) => (
  <OTPInput
    ref={ref}
    containerClassName={cn(
      "flex items-center gap-2 has-[:disabled]:opacity-50",
      containerClassName
    )}
    className={cn("disabled:cursor-not-allowed", className)}
    {...props}
  />
))
InputOTP.displayName = "InputOTP"

const InputOTPGroup = React.forwardRef<
  React.ElementRef<"div">,
  React.ComponentPropsWithoutRef<"div">
>(({ className, ...props }, ref) => (
  <div ref={ref} className={cn("flex items-center", className)} {...props} />
))
InputOTPGroup.displayName = "InputOTPGroup"

const InputOTPSlot = React.forwardRef<
  React.ElementRef<"div">,
  React.ComponentPropsWithoutRef<"div"> & { index: number }
>(({ index, className, ...props }, ref) => {
  const inputOTPContext = React.useContext(OTPInputContext)
  const { char, hasFakeCaret, isActive } = inputOTPContext.slots[index]

  return (
    <div
      ref={ref}
      className={cn(
        "relative flex h-10 w-10 items-center justify-center border-y border-r border-input text-sm transition-all first:rounded-l-md first:border-l last:rounded-r-md",
        isActive && "z-10 ring-2 ring-ring ring-offset-background",
        className
      )}
      {...props}
    >
      {char}
      {hasFakeCaret && (
        <div className="pointer-events-none absolute inset-0 flex items-center justify-center">
          <div className="h-4 w-px animate-caret-blink bg-foreground duration-1000" />
        </div>
      )}
    </div>
  )
})
InputOTPSlot.displayName = "InputOTPSlot"

const InputOTPSeparator = React.forwardRef<
  React.ElementRef<"div">,
  React.ComponentPropsWithoutRef<"div">
>(({ ...props }, ref) => (
  <div ref={ref} role="separator" {...props}>
    <Dot />
  </div>
))
InputOTPSeparator.displayName = "InputOTPSeparator"

export { InputOTP, InputOTPGroup, InputOTPSlot, InputOTPSeparator }

client/src/components/ui/badge.tsx.content:
import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const badgeVariants = cva(
  // Whitespace-nowrap: Badges should never wrap.
  "whitespace-nowrap inline-flex items-center rounded-md border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2" +
  " hover-elevate " ,
  {
    variants: {
      variant: {
        default:
          "border-transparent bg-primary text-primary-foreground shadow-xs",
        secondary: "border-transparent bg-secondary text-secondary-foreground",
        destructive:
          "border-transparent bg-destructive text-destructive-foreground shadow-xs",

        outline: " border [border-color:var(--badge-outline)] shadow-xs",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  },
)

export interface BadgeProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof badgeVariants> {}

function Badge({ className, variant, ...props }: BadgeProps) {
  return (
    <div className={cn(badgeVariants({ variant }), className)} {...props} />
  );
}

export { Badge, badgeVariants }

client/src/pages/AdminDashboardPage.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { BarChart3, Users, Home, Store, LogOut, Check, X, Trash2, Edit } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface Condominium {
  id: string;
  name: string;
  address: string;
  city: string;
  state: string;
  units: number;
  phone?: string;
  email?: string;
  status: "pending" | "approved" | "rejected";
  createdAt?: string;
}

interface User {
  id: string;
  username: string;
  role: string;
  condoId?: string;
  createdAt?: string;
}

interface Store {
  id: string;
  name: string;
  category: string;
  userId: string;
  status: string;
}

interface Stats {
  residents: number;
  stores: number;
  services: number;
  todayOrders: number;
}

export default function AdminDashboardPage() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [formData, setFormData] = useState({ title: "", content: "" });

  const { data: pendingCondos = [] } = useQuery<Condominium[]>({
    queryKey: ["/api/condominiums/pending/list"],
  });

  const { data: stats } = useQuery<Stats>({
    queryKey: ["/api/admin/stats"],
  });

  const { data: users = [], refetch: refetchUsers } = useQuery<User[]>({
    queryKey: ["/api/admin/usuarios"],
  });

  const { data: stores = [], refetch: refetchStores } = useQuery<Store[]>({
    queryKey: ["/api/admin/stores"],
  });

  const approveMutation = useMutation({
    mutationFn: async (condoId: string) => {
      const response = await fetch(`/api/condominiums/${condoId}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ status: "approved" }),
      });
      if (!response.ok) throw new Error("Erro ao aprovar");
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Sucesso", description: "Condom√≠nio aprovado" });
    },
  });

  const rejectMutation = useMutation({
    mutationFn: async (condoId: string) => {
      const response = await fetch(`/api/condominiums/${condoId}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ status: "rejected" }),
      });
      if (!response.ok) throw new Error("Erro ao rejeitar");
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Sucesso", description: "Condom√≠nio rejeitado" });
    },
  });

  const deleteUserMutation = useMutation({
    mutationFn: async (userId: string) => {
      const token = localStorage.getItem("token");
      const response = await fetch(`/api/admin/moradores/${userId}`, {
        method: "DELETE",
        headers: { "Authorization": `Bearer ${token}` },
      });
      if (!response.ok) throw new Error("Erro ao excluir usu√°rio");
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Sucesso", description: "Usu√°rio exclu√≠do" });
      refetchUsers();
    },
  });

  const deleteStoreMutation = useMutation({
    mutationFn: async (storeId: string) => {
      const token = localStorage.getItem("token");
      const response = await fetch(`/api/admin/stores/${storeId}`, {
        method: "DELETE",
        headers: { "Authorization": `Bearer ${token}` },
      });
      if (!response.ok) throw new Error("Erro ao excluir loja");
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Sucesso", description: "Loja exclu√≠da" });
      refetchStores();
    },
  });

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setLocation("/");
  };

  return (
    <div className="min-h-screen bg-background">
      <header className="sticky top-0 z-40 bg-card border-b">
        <div className="max-w-7xl mx-auto px-4 py-4 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <Home className="w-6 h-6 text-primary" />
            <h1 className="text-2xl font-bold">CondoPlace - Admin</h1>
          </div>
          <Button variant="outline" size="sm" onClick={handleLogout} data-testid="button-logout">
            <LogOut className="w-4 h-4 mr-2" />
            Sair
          </Button>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-4 py-8">
        <Tabs defaultValue="overview" className="w-full">
          <TabsList className="grid w-full grid-cols-6">
            <TabsTrigger value="overview" data-testid="tab-overview">
              <BarChart3 className="w-4 h-4 mr-2" />
              Vis√£o Geral
            </TabsTrigger>
            <TabsTrigger value="requests" data-testid="tab-requests">
              Solicita√ß√µes
            </TabsTrigger>
            <TabsTrigger value="condo" data-testid="tab-condo">
              <Home className="w-4 h-4 mr-2" />
              Condom√≠nio
            </TabsTrigger>
            <TabsTrigger value="residents" data-testid="tab-residents">
              <Users className="w-4 h-4 mr-2" />
              Moradores
            </TabsTrigger>
            <TabsTrigger value="stores" data-testid="tab-stores">
              <Store className="w-4 h-4 mr-2" />
              Lojas
            </TabsTrigger>
            <TabsTrigger value="communications" data-testid="tab-comms">
              Comunicados
            </TabsTrigger>
          </TabsList>

          {/* OVERVIEW */}
          <TabsContent value="overview" className="space-y-6 mt-6">
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <Card className="p-6">
                <h3 className="text-sm font-medium text-muted-foreground">Moradores</h3>
                <p className="text-3xl font-bold mt-2">{stats?.residents || 0}</p>
              </Card>
              <Card className="p-6">
                <h3 className="text-sm font-medium text-muted-foreground">Lojas Ativas</h3>
                <p className="text-3xl font-bold mt-2">{stats?.stores || 0}</p>
              </Card>
              <Card className="p-6">
                <h3 className="text-sm font-medium text-muted-foreground">Servi√ßos</h3>
                <p className="text-3xl font-bold mt-2">{stats?.services || 0}</p>
              </Card>
              <Card className="p-6">
                <h3 className="text-sm font-medium text-muted-foreground">Pedidos Hoje</h3>
                <p className="text-3xl font-bold mt-2">{stats?.todayOrders || 0}</p>
              </Card>
            </div>
          </TabsContent>

          {/* REQUESTS */}
          <TabsContent value="requests" className="space-y-6 mt-6">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-lg font-semibold">Solicita√ß√µes Pendentes</h2>
              <Badge variant="secondary">{pendingCondos.length} condom√≠nios</Badge>
            </div>

            {pendingCondos.length === 0 ? (
              <Card className="p-8 text-center">
                <p className="text-muted-foreground">Nenhuma solicita√ß√£o pendente</p>
              </Card>
            ) : (
              <div className="space-y-4">
                {pendingCondos.map((condo) => (
                  <Card key={condo.id} className="p-6" data-testid={`card-condo-request-${condo.id}`}>
                    <div className="flex justify-between items-start mb-4">
                      <div className="flex-1">
                        <h3 className="font-semibold text-lg">{condo.name}</h3>
                        <p className="text-sm text-muted-foreground mt-1">
                          {condo.address}, {condo.city} - {condo.state}
                        </p>
                        <div className="flex gap-4 mt-2 text-sm text-muted-foreground">
                          <span>{condo.units} unidades</span>
                          {condo.phone && <span>Tel: {condo.phone}</span>}
                        </div>
                      </div>
                      <Badge variant="outline">Pendente</Badge>
                    </div>

                    <div className="flex gap-2">
                      <Button
                        size="sm"
                        onClick={() => approveMutation.mutate(condo.id)}
                        disabled={approveMutation.isPending}
                        data-testid={`button-approve-${condo.id}`}
                      >
                        <Check className="w-4 h-4 mr-2" />
                        Aprovar
                      </Button>
                      <Button
                        size="sm"
                        variant="destructive"
                        onClick={() => rejectMutation.mutate(condo.id)}
                        disabled={rejectMutation.isPending}
                        data-testid={`button-reject-${condo.id}`}
                      >
                        <X className="w-4 h-4 mr-2" />
                        Rejeitar
                      </Button>
                    </div>
                  </Card>
                ))}
              </div>
            )}
          </TabsContent>

          {/* CONDOMINIUM */}
          <TabsContent value="condo" className="space-y-6 mt-6">
            <Card className="p-6">
              <h2 className="text-lg font-semibold mb-6">Dados do Condom√≠nio</h2>
              <div className="space-y-4">
                <div>
                  <Label>Nome do Condom√≠nio</Label>
                  <Input placeholder="Ex: Condominio Acqua Sena" data-testid="input-condo-name" />
                </div>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label>Cidade</Label>
                    <Input placeholder="S√£o Paulo" data-testid="input-city" />
                  </div>
                  <div>
                    <Label>Estado</Label>
                    <Input placeholder="SP" maxLength={2} data-testid="input-state" />
                  </div>
                </div>
                <div>
                  <Label>Endere√ßo</Label>
                  <Input placeholder="Rua das Flores, 100" data-testid="input-address" />
                </div>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label>Unidades</Label>
                    <Input type="number" placeholder="120" data-testid="input-units" />
                  </div>
                  <div>
                    <Label>Telefone</Label>
                    <Input placeholder="(11) 1234-5678" data-testid="input-phone" />
                  </div>
                </div>
                <Button className="w-full" data-testid="button-save-condo">Salvar Altera√ß√µes</Button>
              </div>
            </Card>
          </TabsContent>

          {/* RESIDENTS */}
          <TabsContent value="residents" className="space-y-6 mt-6">
            <Card className="p-6">
              <h2 className="text-lg font-semibold mb-4">Gerenciar Moradores</h2>
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead>
                    <tr className="border-b">
                      <th className="text-left p-2">Nome</th>
                      <th className="text-left p-2">Username</th>
                      <th className="text-left p-2">Email</th>
                      <th className="text-left p-2">Status</th>
                      <th className="text-left p-2">A√ß√µes</th>
                    </tr>
                  </thead>
                  <tbody>
                    {users.map((user) => (
                      <tr key={user.id} className="border-b hover:bg-muted/50">
                        <td className="p-2">{(user as any).name || '-'}</td>
                        <td className="p-2">{user.username}</td>
                        <td className="p-2">{(user as any).email || '-'}</td>
                        <td className="p-2">
                          <Badge variant={(user as any).status === 'active' ? 'default' : 'secondary'}>
                            {(user as any).status === 'blocked_until_18' ? 'Bloqueado (<18)' : ((user as any).status || 'active')}
                          </Badge>
                        </td>
                        <td className="p-2 flex gap-2">
                          <Button 
                            size="sm" 
                            variant="destructive" 
                            onClick={() => deleteUserMutation.mutate(user.id)}
                            disabled={deleteUserMutation.isPending}
                            data-testid={`button-delete-user-${user.id}`}
                          >
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </td>
                      </tr>
                    ))}
                    {users.length === 0 && (
                      <tr>
                        <td colSpan={5} className="p-4 text-center text-muted-foreground">
                          Nenhum morador cadastrado
                        </td>
                      </tr>
                    )}
                  </tbody>
                </table>
              </div>
            </Card>
          </TabsContent>

          {/* STORES */}
          <TabsContent value="stores" className="space-y-6 mt-6">
            <Card className="p-6">
              <h2 className="text-lg font-semibold mb-4">Gerenciar Lojas</h2>
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead>
                    <tr className="border-b">
                      <th className="text-left p-2">Nome</th>
                      <th className="text-left p-2">Categoria</th>
                      <th className="text-left p-2">Status</th>
                      <th className="text-left p-2">A√ß√µes</th>
                    </tr>
                  </thead>
                  <tbody>
                    {stores.map((store) => (
                      <tr key={store.id} className="border-b hover:bg-muted/50">
                        <td className="p-2">{store.name}</td>
                        <td className="p-2">{store.category}</td>
                        <td className="p-2">
                          <Badge variant={store.status === 'active' ? 'default' : 'secondary'}>
                            {store.status}
                          </Badge>
                        </td>
                        <td className="p-2 flex gap-2">
                          <Button 
                            size="sm" 
                            variant="destructive" 
                            onClick={() => deleteStoreMutation.mutate(store.id)}
                            disabled={deleteStoreMutation.isPending}
                            data-testid={`button-delete-store-${store.id}`}
                          >
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </td>
                      </tr>
                    ))}
                    {stores.length === 0 && (
                      <tr>
                        <td colSpan={4} className="p-4 text-center text-muted-foreground">
                          Nenhuma loja cadastrada
                        </td>
                      </tr>
                    )}
                  </tbody>
                </table>
              </div>
            </Card>
          </TabsContent>

          {/* COMMUNICATIONS */}
          <TabsContent value="communications" className="space-y-6 mt-6">
            <Card className="p-6">
              <h2 className="text-lg font-semibold mb-6">Criar Comunicado</h2>
              <div className="space-y-4">
                <div>
                  <Label>T√≠tulo</Label>
                  <Input
                    placeholder="T√≠tulo do comunicado"
                    value={formData.title}
                    onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                    data-testid="input-communication-title"
                  />
                </div>
                <div>
                  <Label>Conte√∫do</Label>
                  <Textarea
                    placeholder="Mensagem para os moradores..."
                    value={formData.content}
                    onChange={(e) => setFormData({ ...formData, content: e.target.value })}
                    data-testid="input-communication-content"
                  />
                </div>
                <Button className="w-full" data-testid="button-publish-communication">
