import { sql } from "drizzle-orm";
import { pgTable, text, varchar, integer, decimal, boolean, timestamp, jsonb } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// ‚úÖ USERS TABLE
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
  name: text("name").notNull(),
  email: text("email").notNull().unique(),
  phone: varchar("phone", { length: 20 }).notNull(),
  birthDate: varchar("birth_date", { length: 10 }), // YYYY-MM-DD format
  block: varchar("block", { length: 20 }), // Bloco (ex: "A", "B", "0" para funcion√°rios)
  unit: varchar("unit", { length: 10 }), // Apartamento (ex: "101", "00" para porteiros)
  accountType: varchar("account_type", { length: 20 }).notNull().default("adult"), // adult, minor
  parentAccountId: varchar("parent_account_id"), // ID do adulto respons√°vel (para menores)
  relationship: varchar("relationship", { length: 50 }), // filho, filha, dependente, etc (para menores)
  role: varchar("role", { length: 20 }).notNull().default("resident"), // resident, vendor, service_provider, delivery_person, staff, admin
  status: varchar("status", { length: 20 }).notNull().default("active"), // active, blocked_until_18
  condoId: varchar("condo_id"),
  createdAt: timestamp("created_at").defaultNow(),
});

// ‚úÖ CONDOMINIUMS TABLE
export const condominiums = pgTable("condominiums", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  address: text("address").notNull(),
  city: text("city").notNull(),
  state: text("state").notNull(),
  zipCode: varchar("zip_code", { length: 10 }).notNull(),
  units: integer("units").notNull(),
  phone: varchar("phone", { length: 20 }),
  email: text("email"),
  description: text("description"),
  image: text("image"),
  status: varchar("status", { length: 20 }).notNull().default("pending"), // pending, approved, rejected
  createdAt: timestamp("created_at").defaultNow(),
});

// ‚úÖ STORES TABLE (Lojas de Vendedores)
export const stores = pgTable("stores", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull(),
  name: text("name").notNull(),
  description: text("description"),
  image: text("image"),
  category: varchar("category", { length: 50 }).notNull(),
  phone: varchar("phone", { length: 20 }),
  email: text("email"),
  status: varchar("status", { length: 20 }).notNull().default("active"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// ‚úÖ STORE CONDOMINIUM JUNCTION TABLE (lojas em m√∫ltiplos condom√≠nios)
export const storeCondominiums = pgTable("store_condominiums", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  storeId: varchar("store_id").notNull(),
  condoId: varchar("condo_id").notNull(),
  status: varchar("status", { length: 20 }).notNull().default("pending"), // pending, approved, active
  createdAt: timestamp("created_at").defaultNow(),
});

// ‚úÖ PRODUCTS TABLE (Produtos das Lojas)
export const products = pgTable("products", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  storeId: varchar("store_id").notNull(),
  name: text("name").notNull(),
  description: text("description"),
  image: text("image"),
  category: varchar("category", { length: 50 }),
  price: decimal("price", { precision: 10, scale: 2 }).notNull(),
  ingredients: text("ingredients"),
  available: boolean("available").notNull().default(true),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// ‚úÖ SERVICE PROVIDERS TABLE (Prestadores de Servi√ßo)
export const serviceProviders = pgTable("service_providers", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull(),
  name: text("name").notNull(),
  description: text("description"),
  image: text("image"),
  serviceType: varchar("service_type", { length: 50 }).notNull(),
  phone: varchar("phone", { length: 20 }),
  email: text("email"),
  status: varchar("status", { length: 20 }).notNull().default("active"),
  rating: decimal("rating", { precision: 3, scale: 2 }).default("0"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// ‚úÖ SERVICE PROVIDER CONDOMINIUM JUNCTION TABLE
export const serviceProviderCondominiums = pgTable("service_provider_condominiums", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  providerId: varchar("provider_id").notNull(),
  condoId: varchar("condo_id").notNull(),
  status: varchar("status", { length: 20 }).notNull().default("pending"),
  createdAt: timestamp("created_at").defaultNow(),
});

// ‚úÖ SERVICES TABLE (Servi√ßos Oferecidos)
export const services = pgTable("services", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  providerId: varchar("provider_id").notNull(),
  name: text("name").notNull(),
  description: text("description"),
  image: text("image"),
  price: decimal("price", { precision: 10, scale: 2 }).notNull(),
  duration: integer("duration"),
  available: boolean("available").notNull().default(true),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// ‚úÖ DELIVERY PERSONS TABLE (Entregadores)
export const deliveryPersons = pgTable("delivery_persons", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull(),
  name: text("name").notNull(),
  image: text("image"),
  phone: varchar("phone", { length: 20 }),
  block: varchar("block", { length: 20 }),
  unit: varchar("unit", { length: 10 }),
  status: varchar("status", { length: 20 }).notNull().default("offline"), // online, offline
  rating: decimal("rating", { precision: 3, scale: 2 }).default("0"),
  totalDeliveries: integer("total_deliveries").default(0),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// ‚úÖ DELIVERY PERSON CONDOMINIUM JUNCTION TABLE
export const deliveryPersonCondominiums = pgTable("delivery_person_condominiums", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  deliveryPersonId: varchar("delivery_person_id").notNull(),
  condoId: varchar("condo_id").notNull(),
  status: varchar("status", { length: 20 }).notNull().default("active"),
  createdAt: timestamp("created_at").defaultNow(),
});

// ‚úÖ ORDERS TABLE (Pedidos)
export const orders = pgTable("orders", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  condoId: varchar("condo_id").notNull(),
  storeId: varchar("store_id").notNull(),
  residentId: varchar("resident_id").notNull(),
  deliveryPersonId: varchar("delivery_person_id"),
  status: varchar("status", { length: 20 }).notNull().default("pending"), // pending, confirmed, preparing, ready, on_way, delivered, cancelled
  totalPrice: decimal("total_price", { precision: 10, scale: 2 }).notNull(),
  items: jsonb("items").notNull(), // array of {productId, name, price, quantity}
  deliveryAddress: text("delivery_address"),
  notes: text("notes"),
  tip: decimal("tip", { precision: 10, scale: 2 }).default("0"),
  rating: integer("rating"), // 1-5
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// ‚úÖ APPOINTMENTS TABLE (Agendamentos)
export const appointments = pgTable("appointments", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  condoId: varchar("condo_id").notNull(),
  serviceId: varchar("service_id").notNull(),
  providerId: varchar("provider_id").notNull(),
  residentId: varchar("resident_id").notNull(),
  status: varchar("status", { length: 20 }).notNull().default("pending"), // pending, confirmed, completed, cancelled
  scheduledAt: timestamp("scheduled_at").notNull(),
  notes: text("notes"),
  rating: integer("rating"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// ‚úÖ COMMUNICATIONS TABLE (Comunicados)
export const communications = pgTable("communications", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  condoId: varchar("condo_id").notNull(),
  title: text("title").notNull(),
  content: text("content").notNull(),
  image: text("image"),
  status: varchar("status", { length: 20 }).notNull().default("published"),
  createdBy: varchar("created_by").notNull(),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// ‚úÖ RATINGS TABLE (Avalia√ß√µes)
export const ratings = pgTable("ratings", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  condoId: varchar("condo_id").notNull(),
  targetType: varchar("target_type", { length: 20 }).notNull(), // product, store, service, delivery_person
  targetId: varchar("target_id").notNull(),
  residentId: varchar("resident_id").notNull(),
  score: integer("score").notNull(), // 1-5
  comment: text("comment"),
  createdAt: timestamp("created_at").defaultNow(),
});

// ‚úÖ MARKETPLACE ITEMS TABLE (Vendas/Doa√ß√µes/Trocas entre moradores)
export const marketplaceItems = pgTable("marketplace_items", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  condoId: varchar("condo_id").notNull(),
  userId: varchar("user_id").notNull(),
  title: text("title").notNull(),
  description: text("description"),
  images: jsonb("images").default([]), // array de URLs de imagens
  category: varchar("category", { length: 50 }), // roupas, m√≥veis, eletr√¥nicos, etc
  type: varchar("type", { length: 20 }).notNull(), // sale, donation, exchange
  price: decimal("price", { precision: 10, scale: 2 }), // null para doa√ß√µes
  block: varchar("block", { length: 20 }),
  unit: varchar("unit", { length: 10 }),
  status: varchar("status", { length: 20 }).notNull().default("available"), // available, sold, reserved, removed
  views: integer("views").default(0),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// ‚úÖ LOST AND FOUND TABLE (Achados & Perdidos)
export const lostAndFound = pgTable("lost_and_found", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  condoId: varchar("condo_id").notNull(),
  userId: varchar("user_id").notNull(), // quem achou/perdeu
  type: varchar("type", { length: 20 }).notNull(), // lost, found
  title: text("title").notNull(),
  description: text("description").notNull(),
  images: jsonb("images").default([]), // array de URLs
  category: varchar("category", { length: 50 }), // chaves, documentos, pets, eletr√¥nicos, etc
  locationFound: text("location_found"), // onde foi encontrado (bloco, √°rea comum, etc)
  block: varchar("block", { length: 20 }),
  contactInfo: text("contact_info"), // como contatar
  status: varchar("status", { length: 20 }).notNull().default("active"), // active, resolved, expired
  resolvedAt: timestamp("resolved_at"),
  createdAt: timestamp("created_at").defaultNow(),
});

// ‚úÖ REPORTS TABLE (Den√∫ncias)
export const reports = pgTable("reports", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  condoId: varchar("condo_id").notNull(),
  reporterId: varchar("reporter_id").notNull(), // quem denunciou
  targetType: varchar("target_type", { length: 30 }).notNull(), // product, store, service, delivery_person, user, marketplace_item, lost_and_found, message
  targetId: varchar("target_id").notNull(), // ID do item denunciado
  reason: varchar("reason", { length: 50 }).notNull(), // inappropriate_product, misconduct, fraud, harassment, offensive_content, prohibited_content, delivery_issue, abusive_price, rule_violation, danger, minor_misuse
  description: text("description").notNull(),
  evidence: jsonb("evidence").default([]), // screenshots, fotos, etc
  status: varchar("status", { length: 20 }).notNull().default("pending"), // pending, under_review, resolved, dismissed
  adminNotes: text("admin_notes"),
  resolvedBy: varchar("resolved_by"),
  resolvedAt: timestamp("resolved_at"),
  createdAt: timestamp("created_at").defaultNow(),
});

// ‚úÖ ZOD SCHEMAS
export const insertUserSchema = createInsertSchema(users)
  .pick({
    username: true,
    password: true,
    name: true,
    email: true,
    phone: true,
    birthDate: true,
    block: true,
    unit: true,
    accountType: true,
    parentAccountId: true,
    relationship: true,
    role: true,
    status: true,
    condoId: true,
  })
  .extend({
    // Valida√ß√£o customizada para data de nascimento
    birthDate: z.string()
      .regex(/^\d{4}-\d{2}-\d{2}$/, "Data deve estar no formato YYYY-MM-DD")
      .refine((date) => {
        const dateObj = new Date(date);
        return !isNaN(dateObj.getTime());
      }, "Data de nascimento inv√°lida")
      .refine((date) => {
        const dateObj = new Date(date);
        const today = new Date();
        return dateObj <= today;
      }, "Data de nascimento n√£o pode ser no futuro")
      .refine((date) => {
        const dateObj = new Date(date);
        const today = new Date();
        const age = today.getFullYear() - dateObj.getFullYear();
        return age >= 0 && age <= 120;
      }, "Idade deve estar entre 0 e 120 anos"),

    // Valida√ß√£o para bloco e unidade
    block: z.string().min(1, "Bloco √© obrigat√≥rio").max(20, "Bloco muito longo"),
    unit: z.string().min(1, "Unidade/Apartamento √© obrigat√≥rio").max(10, "Unidade muito longa"),
  });

// Schema base gerado pelo Drizzle
const baseInsertMarketplaceItemSchema = createInsertSchema(marketplaceItems).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

// Schema para inser√ß√£o com valida√ß√µes customizadas
export const insertMarketplaceItemSchema = baseInsertMarketplaceItemSchema
  .extend({
    title: z.string().min(1, "T√≠tulo √© obrigat√≥rio").max(200, "T√≠tulo muito longo"),
    type: z.enum(["sale", "donation", "exchange"], { required_error: "Tipo √© obrigat√≥rio" }),
    price: z.string().nullable().optional(),
    category: z.string().max(100, "Categoria muito longa").nullable().optional(),
    description: z.string().max(2000, "Descri√ß√£o muito longa").nullable().optional(),
    images: z.array(z.string()).nullable().optional(),
  })
  .refine(
    (data) => {
      // Se for venda, pre√ßo √© obrigat√≥rio
      if (data.type === "sale") {
        return !!data.price && parseFloat(data.price) > 0;
      }
      return true;
    },
    {
      message: "Pre√ßo √© obrigat√≥rio para vendas",
      path: ["price"],
    }
  );

// Schema para atualiza√ß√£o (todos os campos opcionais, exceto tipo)
export const updateMarketplaceItemSchema = z
  .object({
    title: z.string().min(1, "T√≠tulo √© obrigat√≥rio").max(200, "T√≠tulo muito longo").optional(),
    description: z.string().max(2000, "Descri√ß√£o muito longa").nullable().optional(),
    category: z.string().max(100, "Categoria muito longa").nullable().optional(),
    price: z.string().nullable().optional(),
    status: z.enum(["available", "sold", "reserved", "removed"]).optional(),
    images: z.array(z.string()).nullable().optional(),
  })
  .refine(
    (data) => {
      // Se houver pre√ßo, deve ser v√°lido
      if (data.price !== undefined && data.price !== null) {
        const numPrice = parseFloat(data.price);
        return !isNaN(numPrice) && numPrice >= 0;
      }
      return true;
    },
    {
      message: "Pre√ßo inv√°lido",
      path: ["price"],
    }
  );

export const insertLostAndFoundSchema = createInsertSchema(lostAndFound).omit({
  id: true,
  createdAt: true,
});

export const insertReportSchema = createInsertSchema(reports).omit({
  id: true,
  createdAt: true,
});

export const insertCondoSchema = createInsertSchema(condominiums).omit({
  id: true,
  createdAt: true,
});

export const insertStoreSchema = createInsertSchema(stores).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertProductSchema = createInsertSchema(products).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertServiceProviderSchema = createInsertSchema(serviceProviders).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertServiceSchema = createInsertSchema(services).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertDeliveryPersonSchema = createInsertSchema(deliveryPersons).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertOrderSchema = createInsertSchema(orders).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertAppointmentSchema = createInsertSchema(appointments).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

// ‚úÖ TYPES
export type User = typeof users.$inferSelect;
export type InsertUser = z.infer<typeof insertUserSchema>;

export type Condominium = typeof condominiums.$inferSelect;
export type InsertCondominium = z.infer<typeof insertCondoSchema>;

export type Store = typeof stores.$inferSelect;
export type InsertStore = z.infer<typeof insertStoreSchema>;

export type Product = typeof products.$inferSelect;
export type InsertProduct = z.infer<typeof insertProductSchema>;

export type ServiceProvider = typeof serviceProviders.$inferSelect;
export type InsertServiceProvider = z.infer<typeof insertServiceProviderSchema>;

export type Service = typeof services.$inferSelect;
export type InsertService = z.infer<typeof insertServiceSchema>;

export type DeliveryPerson = typeof deliveryPersons.$inferSelect;
export type InsertDeliveryPerson = z.infer<typeof insertDeliveryPersonSchema>;

export type Order = typeof orders.$inferSelect;
export type InsertOrder = z.infer<typeof insertOrderSchema>;

export type Appointment = typeof appointments.$inferSelect;
export type InsertAppointment = z.infer<typeof insertAppointmentSchema>;

export type MarketplaceItem = typeof marketplaceItems.$inferSelect;
export type InsertMarketplaceItem = z.infer<typeof insertMarketplaceItemSchema>;

export type LostAndFoundItem = typeof lostAndFound.$inferSelect;
export type InsertLostAndFoundItem = z.infer<typeof insertLostAndFoundSchema>;

export type Report = typeof reports.$inferSelect;
export type InsertReport = z.infer<typeof insertReportSchema>;

