import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { AlertCircle, Plus, Search, LogIn } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface Condominium {
  id: string;
  name: string;
  address: string;
  city: string;
  units: number;
}

export default function CondoSelectorPage() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [search, setSearch] = useState("");
  const [condos, setCondos] = useState<Condominium[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Buscar condominios aprovados
    const loadCondos = async () => {
      try {
        const response = await fetch("/api/condominiums");
        if (response.ok) {
          const data = await response.json();
          setCondos(data);
        }
      } catch (error) {
        console.error("Erro ao carregar condom√≠nios:", error);
      } finally {
        setLoading(false);
      }
    };
    loadCondos();
  }, []);

  const handleBackToHome = () => {
    const isLoggedIn = !!localStorage.getItem("token");
    if (!isLoggedIn) {
      setLocation("/");
    }
  };

  const filteredCondos = condos.filter((condo) =>
    condo.name.toLowerCase().includes(search.toLowerCase())
  );

  const handleSelectCondo = (condoId: string) => {
    localStorage.setItem("selectedCondoId", condoId);
    setLocation("/register");
  };

  const handleRequestNewCondo = () => {
    setLocation("/register-condo");
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-pulse">
          <div className="h-12 w-12 bg-primary rounded-full"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background py-8">
      <div className="max-w-2xl mx-auto px-4">
        <div className="flex items-center justify-between mb-8">
          <div className="flex-1">
            <h1 className="text-3xl font-bold mb-2">Selecione seu Condom√≠nio</h1>
            <p className="text-muted-foreground">Escolha o condom√≠nio onde voc√™ reside</p>
          </div>
          <div className="flex gap-2">
            <Button
              variant="ghost"
              size="sm"
              onClick={handleBackToHome}
              data-testid="button-back"
              className="text-xs"
            >
              Voltar
            </Button>
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setLocation("/admin/login")}
              data-testid="button-admin-login"
              className="text-xs"
            >
              Admin
            </Button>
          </div>
        </div>

        <Card className="p-6 mb-6">
          <div className="space-y-4">
            <div className="relative">
              <Search className="absolute left-3 top-3 w-4 h-4 text-muted-foreground" />
              <Input
                placeholder="Buscar condom√≠nio..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                className="pl-10"
                data-testid="input-search-condo"
              />
            </div>

            {filteredCondos.length === 0 ? (
              <div className="text-center py-12">
                <AlertCircle className="w-12 h-12 mx-auto text-muted-foreground mb-4" />
                <p className="text-muted-foreground mb-2">Nenhum condom√≠nio encontrado</p>
                <p className="text-sm text-muted-foreground mb-4">
                  Seu condom√≠nio n√£o est√° registrado ainda?
                </p>
                <Button
                  onClick={handleRequestNewCondo}
                  data-testid="button-register-new-condo"
                >
                  <Plus className="w-4 h-4 mr-2" />
                  Solicitar Registro de Novo Condom√≠nio
                </Button>
              </div>
            ) : (
              <div className="space-y-3">
                {filteredCondos.map((condo) => (
                  <button
                    key={condo.id}
                    onClick={() => handleSelectCondo(condo.id)}
