  const { toast } = useToast();

  const [appointments, setAppointments] = useState<Appointment[]>([
    {
      id: '1',
      serviceProviderName: 'Studio da Ana',
      serviceType: 'Corte de Cabelo',
      date: new Date(Date.now() + 2 * 24 * 60 * 60 * 1000),
      time: '14:00',
      location: 'Apto 405',
      status: 'scheduled'
    },
    {
      id: '2',
      serviceProviderName: 'Eletricista Carlos',
      serviceType: 'Reparo El√©trico',
      date: new Date(Date.now() + 5 * 24 * 60 * 60 * 1000),
      time: '10:00',
      location: 'Apto 301',
      status: 'confirmed'
    },
    {
      id: '3',
      serviceProviderName: 'Barbearia do Jo√£o',
      serviceType: 'Barba e Cabelo',
      date: new Date(Date.now() - 3 * 24 * 60 * 60 * 1000),
      time: '16:00',
      location: 'Apto 405',
      status: 'completed'
    }
  ]);

  const upcomingAppointments = appointments.filter(a => ['scheduled', 'confirmed'].includes(a.status));
  const pastAppointments = appointments.filter(a => ['completed', 'cancelled'].includes(a.status));

  const handleCancel = (id: string) => {
    setAppointments(appointments.map(a => 
      a.id === id ? { ...a, status: 'cancelled' as AppointmentStatus } : a
    ));
    toast({
      title: "Agendamento Cancelado",
      description: "Seu agendamento foi cancelado com sucesso.",
      variant: "destructive"
    });
  };

  const handleReschedule = (id: string) => {
    toast({
      title: "Reagendar",
      description: "Funcionalidade de reagendamento em desenvolvimento.",
    });
  };

  return (
    <div className="min-h-screen bg-background pb-20 md:pb-0">
      <header className="sticky top-0 z-40 bg-card border-b">
        <div className="max-w-7xl mx-auto px-4 py-3 flex items-center justify-between">
          <div className="flex items-center gap-2">
            <Button
              variant="ghost"
              size="icon"
              onClick={() => setLocation('/')}
              data-testid="button-back"
            >
              <ArrowLeft className="w-5 h-5" />
            </Button>
            <h1 className="text-xl font-bold">Meus Agendamentos</h1>
          </div>
          <div className="flex gap-2">
            <CartButton itemCount={cartCount} onClick={() => setLocation('/')} />
            <ThemeToggle />
          </div>
        </div>
      </header>

      <main className="max-w-4xl mx-auto px-4 py-6">
        <Tabs defaultValue="upcoming" className="w-full">
          <TabsList className="grid w-full grid-cols-2 mb-6">
            <TabsTrigger value="upcoming" data-testid="tab-upcoming">
              Pr√≥ximos ({upcomingAppointments.length})
            </TabsTrigger>
            <TabsTrigger value="past" data-testid="tab-past">
              Hist√≥rico ({pastAppointments.length})
            </TabsTrigger>
          </TabsList>

          <TabsContent value="upcoming" className="space-y-4">
            {upcomingAppointments.length === 0 ? (
              <div className="text-center py-12 text-muted-foreground">
                Voc√™ n√£o tem agendamentos pr√≥ximos
              </div>
            ) : (
              upcomingAppointments.map(appointment => (
                <AppointmentCard
                  key={appointment.id}
                  {...appointment}
                  onCancel={handleCancel}
                  onReschedule={handleReschedule}
                />
              ))
            )}
          </TabsContent>

          <TabsContent value="past" className="space-y-4">
            {pastAppointments.length === 0 ? (
              <div className="text-center py-12 text-muted-foreground">
                Voc√™ ainda n√£o tem agendamentos conclu√≠dos
              </div>
            ) : (
              pastAppointments.map(appointment => (
                <AppointmentCard
                  key={appointment.id}
                  {...appointment}
                />
              ))
            )}
          </TabsContent>
        </Tabs>
      </main>

      <BottomNav onNavigate={setLocation} />
    </div>
  );
}

