server/index-dev.ts.content:
import fs from "node:fs";
import path from "node:path";
import { type Server } from "node:http";

import { nanoid } from "nanoid";
import { type Express } from "express";
import { createServer as createViteServer, createLogger } from "vite";

import viteConfig from "../vite.config";
import runApp from "./app";

export async function setupVite(app: Express, server: Server) {
  const viteLogger = createLogger();
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

(async () => {
  await runApp(setupVite);
})();

client/src/components/AppointmentCalenda.content:
import { useState } from "react";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { ChevronLeft, ChevronRight } from "lucide-react";
import { Badge } from "@/components/ui/badge";

interface TimeSlot {
  time: string;
  available: boolean;
}

interface AppointmentCalendarProps {
  serviceProviderName: string;
  onSelectSlot: (date: Date, time: string) => void;
}

export default function AppointmentCalendar({
  serviceProviderName,
  onSelectSlot
}: AppointmentCalendarProps) {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState<Date | null>(null);
  const [selectedTime, setSelectedTime] = useState<string | null>(null);

  const getDaysInMonth = (date: Date) => {
    const year = date.getFullYear();
    const month = date.getMonth();
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const daysInMonth = lastDay.getDate();
    const startingDayOfWeek = firstDay.getDay();

    const days = [];
    for (let i = 0; i < startingDayOfWeek; i++) {
      days.push(null);
    }
    for (let i = 1; i <= daysInMonth; i++) {
      days.push(new Date(year, month, i));
    }
    return days;
  };

  const timeSlots: TimeSlot[] = [
    { time: "09:00", available: true },
    { time: "10:00", available: true },
    { time: "11:00", available: false },
    { time: "14:00", available: true },
    { time: "15:00", available: true },
    { time: "16:00", available: true },
    { time: "17:00", available: false },
  ];

  const days = getDaysInMonth(currentDate);
  const weekDays = ['Dom', 'Seg', 'Ter', 'Qua', 'Qui', 'Sex', 'S√°b'];

  const nextMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() + 1));
  };

  const prevMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() - 1));
  };

  const isToday = (date: Date | null) => {
    if (!date) return false;
    const today = new Date();
    return date.toDateString() === today.toDateString();
  };

  const isPast = (date: Date | null) => {
    if (!date) return false;
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    return date < today;
  };

  return (
    <Card className="p-6">
      <div className="mb-6">
        <h3 className="text-lg font-semibold mb-2">Agendar com {serviceProviderName}</h3>
        <p className="text-sm text-muted-foreground">Selecione uma data e hor√°rio dispon√≠vel</p>
      </div>

      <div className="mb-6">
        <div className="flex items-center justify-between mb-4">
          <h4 className="font-semibold">
            {currentDate.toLocaleDateString('pt-BR', { month: 'long', year: 'numeric' })}
          </h4>
          <div className="flex gap-2">
            <Button
              size="icon"
              variant="outline"
              onClick={prevMonth}
              data-testid="button-prev-month"
            >
              <ChevronLeft className="w-4 h-4" />
            </Button>
            <Button
              size="icon"
              variant="outline"
              onClick={nextMonth}
              data-testid="button-next-month"
            >
              <ChevronRight className="w-4 h-4" />
            </Button>
          </div>
        </div>

        <div className="grid grid-cols-7 gap-2 mb-2">
          {weekDays.map((day) => (
            <div key={day} className="text-center text-sm font-medium text-muted-foreground">
              {day}
            </div>
          ))}
        </div>

        <div className="grid grid-cols-7 gap-2">
          {days.map((day, index) => (
            <button
              key={index}
              disabled={!day || isPast(day)}
              onClick={() => {
                if (day) {
                  setSelectedDate(day);
                  setSelectedTime(null);
                }
              }}
              className={`
                aspect-square rounded-md text-sm transition-colors
                ${!day ? 'invisible' : ''}
                ${isPast(day) ? 'text-muted-foreground cursor-not-allowed' : 'hover-elevate'}
                ${isToday(day) ? 'border-2 border-primary' : ''}
                ${selectedDate?.toDateString() === day?.toDateString() 
                  ? 'bg-primary text-primary-foreground' 
                  : 'bg-secondary'}
              `}
              data-testid={day ? `button-date-${day.getDate()}` : undefined}
            >
              {day?.getDate()}
            </button>
          ))}
        </div>
      </div>

      {selectedDate && (
        <div>
          <div className="flex items-center justify-between mb-4">
            <h4 className="font-semibold">
              Hor√°rios dispon√≠veis - {selectedDate.toLocaleDateString('pt-BR', { day: '2-digit', month: 'long' })}
            </h4>
          </div>
          <div className="grid grid-cols-3 gap-2 mb-6">
            {timeSlots.map((slot) => (
              <Button
                key={slot.time}
                variant={selectedTime === slot.time ? "default" : "outline"}
                disabled={!slot.available}
                onClick={() => setSelectedTime(slot.time)}
                data-testid={`button-time-${slot.time}`}
                className="relative"
              >
                {slot.time}
                {!slot.available && (
                  <Badge 
                    variant="secondary" 
                    className="absolute -top-2 -right-2 text-xs px-1"
                  >
                    Ocupado
                  </Badge>
                )}
              </Button>
            ))}
          </div>

          {selectedTime && (
            <Button
              className="w-full"
              onClick={() => {
                onSelectSlot(selectedDate, selectedTime);
              }}
              data-testid="button-confirm-appointment"
            >
              Confirmar Agendamento - {selectedDate.toLocaleDateString('pt-BR')} √†s {selectedTime}
            </Button>
          )}
        </div>
      )}
    </Card>
  );
}

client/src/pages/CheckoutPage.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { Separator } from "@/components/ui/separator";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { useToast } from "@/hooks/use-toast";
import { ArrowLeft, Lock, CheckCircle, Loader } from "lucide-react";

interface CartItem {
  id: string;
  name: string;
  price: number;
  quantity: number;
  store: string;
}

export default function CheckoutPage() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [paymentMethod, setPaymentMethod] = useState<'card' | 'pix'>('card');
  const [processing, setProcessing] = useState(false);
  const [completed, setCompleted] = useState(false);

  // Mock cart items
  const cartItems: CartItem[] = [
    { id: '1', name: 'Brigadeiro Gourmet', price: 3.50, quantity: 2, store: 'Doces da Maria' },
    { id: '2', name: 'P√£o de Queijo', price: 2.00, quantity: 3, store: 'Lanchonete do Seu Jos√©' }
  ];

  const subtotal = cartItems.reduce((sum, item) => sum + (item.price * item.quantity), 0);
  const deliveryFee = 5.00;
  const tax = subtotal * 0.10; // 10% tax
  const total = subtotal + deliveryFee + tax;

  const handlePayment = async (e: React.FormEvent) => {
    e.preventDefault();
    setProcessing(true);

    try {
      if (paymentMethod === 'card') {
        // Chamada ao backend para processar pagamento Stripe
        // O backend ir√° criptografar e tokenizar usando Stripe API
        const response = await fetch('/api/payments/create-payment-intent', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            amount: Math.round(total * 100), // Stripe espera cents
            currency: 'brl',
            items: cartItems,
            metadata: {
              vendorCommission: 0, // 0% comiss√£o (100% vendedor)
              appCommission: 0,
            }
          })
        });

        if (!response.ok) {
          throw new Error('Erro ao criar inten√ß√£o de pagamento');
        }

        const { clientSecret } = await response.json();

        // Aqui seria integrado Stripe Elements ou Stripe.js
        // Por seguran√ßa, o cliente usaria clientSecret para completar pagamento
        // Dados de cart√£o NUNCA deixam o navegador sem criptografia Stripe

        toast({
          title: "Redirecionando para Stripe",
          description: "Voc√™ ser√° redirecionado para completar o pagamento de forma segura.",
        });

        // Simular redirecionamento para Stripe
        await new Promise(resolve => setTimeout(resolve, 2000));
      } else {
        // Para PIX, gerar c√≥digo QR din√¢mico via backend
        const response = await fetch('/api/payments/create-pix-qr', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            amount: total,
            items: cartItems,
          })
        });

        if (!response.ok) {
          throw new Error('Erro ao gerar c√≥digo Pix');
        }

        const { qrCode } = await response.json();

        toast({
          title: "QR Code Pix Gerado",
          description: "Escaneie o c√≥digo para completar o pagamento.",
        });

        await new Promise(resolve => setTimeout(resolve, 2000));
      }

      setCompleted(true);
    } catch (error) {
      toast({
        title: "Erro no Pagamento",
        description: error instanceof Error ? error.message : "Erro ao processar pagamento",
        variant: "destructive"
      });
      setProcessing(false);
    }
  };

  if (completed) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <Card className="p-8 max-w-md w-full text-center space-y-6">
          <div className="flex justify-center">
            <CheckCircle className="w-16 h-16 text-green-600" />
          </div>
          <div>
            <h2 className="text-2xl font-bold mb-2">Pagamento Realizado!</h2>
            <p className="text-muted-foreground mb-2">
              Seu pedido foi confirmado com sucesso.
            </p>
            <p className="text-sm text-muted-foreground mb-4">
              Voc√™ receber√° um email com os detalhes do pedido. A entrega ser√° feita em breve.
            </p>
            <p className="text-lg font-bold text-primary">
              Total: R$ {total.toFixed(2)}
            </p>
          </div>
          <Button 
            onClick={() => setLocation('/orders')}
            className="w-full"
            data-testid="button-track-order"
          >
            Acompanhar Pedido
          </Button>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      <header className="sticky top-0 z-40 bg-card border-b">
        <div className="max-w-4xl mx-auto px-4 py-3 flex items-center gap-2">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => setLocation('/')}
            data-testid="button-back"
          >
            <ArrowLeft className="w-5 h-5" />
          </Button>
          <h1 className="text-xl font-bold">Finalizar Compra</h1>
        </div>
      </header>

      <main className="max-w-4xl mx-auto px-4 py-6">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="md:col-span-2 space-y-6">
            {/* Resumo do Pedido */}
            <Card className="p-6">
              <h2 className="text-lg font-semibold mb-4">Resumo do Pedido</h2>
              <div className="space-y-3">
                {cartItems.map(item => (
                  <div key={item.id} className="flex justify-between text-sm" data-testid={`item-${item.id}`}>
                    <div>
                      <p className="font-medium">{item.name}</p>
                      <p className="text-xs text-muted-foreground">{item.store} x{item.quantity}</p>
                    </div>
                    <p className="font-semibold">R$ {(item.price * item.quantity).toFixed(2)}</p>
                  </div>
                ))}
              </div>
            </Card>

            {/* M√©todo de Pagamento */}
            <Card className="p-6">
              <h2 className="text-lg font-semibold mb-4">M√©todo de Pagamento</h2>

              <div className="space-y-4">
                <label className="flex items-center p-4 border rounded-lg cursor-pointer hover-elevate" data-testid="option-card">
                  <input
                    type="radio"
                    name="payment"
                    value="card"
                    checked={paymentMethod === 'card'}
                    onChange={() => setPaymentMethod('card')}
                    className="mr-3"
                  />
                  <div>
                    <p className="font-medium">Cart√£o de Cr√©dito (Stripe)</p>
                    <p className="text-sm text-muted-foreground">Seguro com criptografia PCI Compliant</p>
                  </div>
                </label>

                <label className="flex items-center p-4 border rounded-lg cursor-pointer hover-elevate" data-testid="option-pix">
                  <input
                    type="radio"
                    name="payment"
                    value="pix"
                    checked={paymentMethod === 'pix'}
                    onChange={() => setPaymentMethod('pix')}
                    className="mr-3"
                  />
                  <div>
                    <p className="font-medium">Pix</p>
                    <p className="text-sm text-muted-foreground">Transfer√™ncia instant√¢nea com QR Code</p>
                  </div>
                </label>
              </div>

              <Separator className="my-6" />

              {/* Aviso de Seguran√ßa */}
              <Alert className="mb-6">
                <Lock className="h-4 w-4" />
                <AlertDescription>
                  ‚úÖ Seus dados de pagamento s√£o processados com seguran√ßa atrav√©s de Stripe (PCI Level 1 Compliant). Dados sens√≠veis nunca s√£o armazenados no nosso servidor.
                </AlertDescription>
              </Alert>

              <form onSubmit={handlePayment} className="space-y-4">
                {paymentMethod === 'card' ? (
                  <div className="p-4 bg-muted rounded-lg border-2 border-dashed">
                    <p className="text-sm text-muted-foreground text-center">
                      Ao clicar em "Pagar", voc√™ ser√° redirecionado para a p√°gina segura do Stripe.
                    </p>
                  </div>
                ) : (
                  <div className="p-4 bg-muted rounded-lg border-2 border-dashed">
                    <p className="text-sm text-muted-foreground text-center">
                      Um QR Code Pix ser√° gerado ap√≥s confirmar o pagamento.
                    </p>
                  </div>
                )}

                <Button 
                  type="submit" 
                  className="w-full mt-4"
                  disabled={processing}
                  data-testid="button-pay"
                >
                  {processing ? (
                    <>
                      <Loader className="w-4 h-4 mr-2 animate-spin" />
                      Processando...
                    </>
                  ) : (
                    `Pagar R$ ${total.toFixed(2)}`
                  )}
                </Button>
              </form>
            </Card>
          </div>

          {/* Resumo de Pre√ßos */}
          <Card className="p-6 h-fit">
            <h3 className="font-semibold mb-4">Total</h3>

            <div className="space-y-3 text-sm mb-4">
              <div className="flex justify-between">
                <span className="text-muted-foreground">Subtotal</span>
                <span>R$ {subtotal.toFixed(2)}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-muted-foreground">Taxa de Entrega</span>
                <span>R$ {deliveryFee.toFixed(2)}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-muted-foreground">Impostos (10%)</span>
                <span>R$ {tax.toFixed(2)}</span>
              </div>

              <Separator />

              <div className="flex justify-between font-bold text-base">
                <span>Total para o Vendedor</span>
                <span className="text-primary">R$ {total.toFixed(2)}</span>
              </div>
            </div>

            <p className="text-xs text-muted-foreground">
              ‚úÖ <strong>100% do valor</strong> vai para o vendedor. O app n√£o cobra comiss√£o.
            </p>
          </Card>
        </div>
      </main>
    </div>
  );
}

