"use client"

import * as React from "react"
import * as LabelPrimitive from "@radix-ui/react-label"
import { Slot } from "@radix-ui/react-slot"
import {
  Controller,
  FormProvider,
  useFormContext,
  type ControllerProps,
  type FieldPath,
  type FieldValues,
} from "react-hook-form"

import { cn } from "@/lib/utils"
import { Label } from "@/components/ui/label"

const Form = FormProvider

type FormFieldContextValue<
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>
> = {
  name: TName
}

const FormFieldContext = React.createContext<FormFieldContextValue>(
  {} as FormFieldContextValue
)

const FormField = <
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>
>({
  ...props
}: ControllerProps<TFieldValues, TName>) => {
  return (
    <FormFieldContext.Provider value={{ name: props.name }}>
      <Controller {...props} />
    </FormFieldContext.Provider>
  )
}

const useFormField = () => {
  const fieldContext = React.useContext(FormFieldContext)
  const itemContext = React.useContext(FormItemContext)
  const { getFieldState, formState } = useFormContext()

  const fieldState = getFieldState(fieldContext.name, formState)

  if (!fieldContext) {
    throw new Error("useFormField should be used within <FormField>")
  }

  const { id } = itemContext

  return {
    id,
    name: fieldContext.name,
    formItemId: `${id}-form-item`,
    formDescriptionId: `${id}-form-item-description`,
    formMessageId: `${id}-form-item-message`,
    ...fieldState,
  }
}

type FormItemContextValue = {
  id: string
}

const FormItemContext = React.createContext<FormItemContextValue>(
  {} as FormItemContextValue
)

const FormItem = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const id = React.useId()

  return (
    <FormItemContext.Provider value={{ id }}>
      <div ref={ref} className={cn("space-y-2", className)} {...props} />
    </FormItemContext.Provider>
  )
})
FormItem.displayName = "FormItem"

const FormLabel = React.forwardRef<
  React.ElementRef<typeof LabelPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof LabelPrimitive.Root>
>(({ className, ...props }, ref) => {
  const { error, formItemId } = useFormField()

  return (
    <Label
      ref={ref}
      className={cn(error && "text-destructive", className)}
      htmlFor={formItemId}
      {...props}
    />
  )
})
FormLabel.displayName = "FormLabel"

const FormControl = React.forwardRef<
  React.ElementRef<typeof Slot>,
  React.ComponentPropsWithoutRef<typeof Slot>
>(({ ...props }, ref) => {
  const { error, formItemId, formDescriptionId, formMessageId } = useFormField()

  return (
    <Slot
      ref={ref}
      id={formItemId}
      aria-describedby={
        !error
          ? `${formDescriptionId}`
          : `${formDescriptionId} ${formMessageId}`
      }
      aria-invalid={!!error}
      {...props}
    />
  )
})
FormControl.displayName = "FormControl"

const FormDescription = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ className, ...props }, ref) => {
  const { formDescriptionId } = useFormField()

  return (
    <p
      ref={ref}
      id={formDescriptionId}
      className={cn("text-sm text-muted-foreground", className)}
      {...props}
    />
  )
})
FormDescription.displayName = "FormDescription"

const FormMessage = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ className, children, ...props }, ref) => {
  const { error, formMessageId } = useFormField()
  const body = error ? String(error?.message ?? "") : children

  if (!body) {
    return null
  }

  return (
    <p
      ref={ref}
      id={formMessageId}
      className={cn("text-sm font-medium text-destructive", className)}
      {...props}
    >
      {body}
    </p>
  )
})
FormMessage.displayName = "FormMessage"

export {
  useFormField,
  Form,
  FormItem,
  FormLabel,
  FormControl,
  FormDescription,
  FormMessage,
  FormField,
}

client/src/components/VendorOrderCard.ts.content:
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Check, X, Clock } from "lucide-react";

interface VendorOrderCardProps {
  id: string;
  customerName: string;
  unit: string;
  items: Array<{ name: string; quantity: number; price: number }>;
  total: number;
  status: 'pending' | 'accepted' | 'preparing' | 'ready' | 'delivered';
  date: Date;
  onAccept?: (id: string) => void;
  onReject?: (id: string) => void;
  onUpdateStatus?: (id: string, status: string) => void;
}

export default function VendorOrderCard({
  id,
  customerName,
  unit,
  items,
  total,
  status,
  date,
  onAccept,
  onReject,
  onUpdateStatus
}: VendorOrderCardProps) {
  return (
    <Card className="p-4" data-testid={`card-vendor-order-${id}`}>
      <div className="flex justify-between items-start mb-4">
        <div>
          <h3 className="font-semibold text-lg" data-testid={`text-customer-${id}`}>
            {customerName}
          </h3>
          <p className="text-sm text-muted-foreground">Unidade {unit}</p>
          <div className="flex items-center gap-1 text-sm text-muted-foreground mt-1">
            <Clock className="w-3 h-3" />
            <span>{date.toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit' })}</span>
          </div>
        </div>
        <Badge variant={status === 'pending' ? 'secondary' : 'default'}>
          {status === 'pending' && 'Novo Pedido'}
          {status === 'accepted' && 'Aceito'}
          {status === 'preparing' && 'Preparando'}
          {status === 'ready' && 'Pronto'}
          {status === 'delivered' && 'Entregue'}
        </Badge>
      </div>

      <div className="space-y-2 mb-4">
        {items.map((item, index) => (
          <div key={index} className="flex justify-between text-sm">
            <span>{item.quantity}x {item.name}</span>
            <span className="text-muted-foreground">R$ {(item.price * item.quantity).toFixed(2)}</span>
          </div>
        ))}
      </div>

      <div className="flex justify-between items-center pt-4 border-t mb-4">
        <span className="font-semibold">Total</span>
        <span className="text-xl font-bold text-primary" data-testid={`text-vendor-order-total-${id}`}>
          R$ {total.toFixed(2)}
        </span>
      </div>

      {status === 'pending' && onAccept && onReject && (
        <div className="flex gap-2">
          <Button
            variant="outline"
            className="flex-1"
            onClick={() => onReject(id)}
            data-testid={`button-reject-${id}`}
          >
            <X className="w-4 h-4 mr-2" />
            Recusar
          </Button>
          <Button
            className="flex-1"
            onClick={() => onAccept(id)}
            data-testid={`button-accept-${id}`}
          >
            <Check className="w-4 h-4 mr-2" />
            Aceitar
          </Button>
        </div>
      )}

      {status === 'accepted' && onUpdateStatus && (
        <Button
          className="w-full"
          onClick={() => onUpdateStatus(id, 'preparing')}
          data-testid={`button-start-preparing-${id}`}
        >
          Iniciar Preparo
        </Button>
      )}

      {status === 'preparing' && onUpdateStatus && (
        <Button
          className="w-full"
          onClick={() => onUpdateStatus(id, 'ready')}
          data-testid={`button-mark-ready-${id}`}
        >
          Marcar como Pronto
        </Button>
      )}

      {status === 'ready' && onUpdateStatus && (
        <Button
          className="w-full"
          variant="secondary"
          onClick={() => onUpdateStatus(id, 'delivered')}
          data-testid={`button-mark-delivered-${id}`}
        >
          Marcar como Entregue
        </Button>
      )}
    </Card>
  );
}

client/src/pages/ProfilePage.tsx.content:
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { User, LogOut, Building2, Edit, Save, X, ArrowLeft, Users } from "lucide-react";
import { apiRequest, queryClient } from "@/lib/queryClient";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import { Badge } from "@/components/ui/badge";

interface UserProfile {
  id: string;
  username: string;
  name: string;
  email: string;
  phone: string;
  role: string;
  status: string;
  condoId: string | null;
}

interface Condominium {
  id: string;
  name: string;
  address: string;
  city: string;
  state: string;
}

export default function ProfilePage() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [isEditing, setIsEditing] = useState(false);
  const [showLogoutDialog, setShowLogoutDialog] = useState(false);
  const [showChangeCondoDialog, setShowChangeCondoDialog] = useState(false);

  const { data: authResponse, isLoading } = useQuery<{ user: UserProfile }>({
    queryKey: ["/api/auth/me"],
  });

  const user = authResponse?.user;

  const { data: condo } = useQuery<Condominium>({
    queryKey: user?.condoId ? [`/api/condominiums/${user.condoId}`] : [],
    enabled: !!user?.condoId,
  });

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
  });

  useEffect(() => {
    if (user && !isEditing) {
      setFormData({
        name: user.name || "",
        email: user.email || "",
        phone: user.phone || "",
      });
    }
  }, [user, isEditing]);

  const updateMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      if (!user?.id) throw new Error("Usu√°rio n√£o identificado");
      const token = localStorage.getItem("token");
      const response = await fetch(`/api/users/${user.id}`, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`,
        },
        body: JSON.stringify(data),
      });
      if (!response.ok) {
        const error = await response.text();
        throw new Error(error || "Falha ao atualizar");
      }
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Sucesso", description: "Perfil atualizado" });
      queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });
      setIsEditing(false);
    },
    onError: (error: Error) => {
      toast({
        title: "Erro",
        description: error.message || "Falha ao atualizar perfil",
        variant: "destructive",
      });
    },
  });

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    localStorage.removeItem("selectedCondoId");
    toast({ title: "At√© logo!", description: "Voc√™ saiu da conta" });
    setLocation("/");
  };

  const handleChangeCondo = () => {
    localStorage.removeItem("selectedCondoId");
    toast({
      title: "Condom√≠nio alterado",
      description: "Selecione um novo condom√≠nio",
    });
    setLocation("/select-condo");
  };

  const handleSave = () => {
    updateMutation.mutate(formData);
  };

  const handleCancel = () => {
    setFormData({
      name: user?.name || "",
      email: user?.email || "",
      phone: user?.phone || "",
    });
    setIsEditing(false);
  };

  const getRoleName = (role: string) => {
    const roles: Record<string, string> = {
      admin: "Administrador",
      vendor: "Vendedor",
      resident: "Morador",
      service_provider: "Prestador de Servi√ßo",
      delivery_person: "Entregador",
    };
    return roles[role] || role;
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p className="text-muted-foreground">Carregando...</p>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p className="text-muted-foreground">Erro ao carregar perfil</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      <header className="sticky top-0 z-40 bg-card border-b">
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <Button
                variant="ghost"
                size="icon"
                onClick={() => setLocation("/")}
                data-testid="button-back"
              >
                <ArrowLeft className="w-5 h-5" />
              </Button>
              <h1 className="text-2xl font-bold">Meu Perfil</h1>
            </div>
          </div>
        </div>
      </header>

      <main className="container mx-auto px-4 py-8 max-w-2xl">
        <div className="space-y-6">
          {/* Informa√ß√µes Pessoais */}
          <Card className="p-6">
            <div className="flex items-center justify-between mb-6">
              <div className="flex items-center gap-3">
                <div className="w-12 h-12 rounded-full bg-primary/10 flex items-center justify-center">
                  <User className="w-6 h-6 text-primary" />
                </div>
                <div>
                  <h2 className="text-xl font-semibold">Informa√ß√µes Pessoais</h2>
                  <p className="text-sm text-muted-foreground">
                    {getRoleName(user.role)}
                  </p>
                </div>
              </div>
              {!isEditing ? (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setIsEditing(true)}
                  data-testid="button-edit-profile"
                >
                  <Edit className="w-4 h-4 mr-2" />
                  Editar
                </Button>
              ) : (
                <div className="flex gap-2">
                  <Button
                    size="sm"
                    onClick={handleSave}
                    disabled={updateMutation.isPending}
                    data-testid="button-save-profile"
                  >
                    <Save className="w-4 h-4 mr-2" />
                    Salvar
                  </Button>
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={handleCancel}
                    data-testid="button-cancel-edit"
                  >
                    <X className="w-4 h-4 mr-2" />
                    Cancelar
                  </Button>
                </div>
              )}
            </div>

            <div className="space-y-4">
              <div>
                <Label>Username</Label>
                <Input
                  value={user.username}
                  disabled
                  className="bg-muted"
                  data-testid="input-username"
                />
                <p className="text-xs text-muted-foreground mt-1">
                  O username n√£o pode ser alterado
                </p>
              </div>

              <div>
                <Label>Nome Completo</Label>
                <Input
                  value={formData.name}
                  onChange={(e) =>
                    setFormData({ ...formData, name: e.target.value })
                  }
                  disabled={!isEditing}
                  data-testid="input-name"
                />
              </div>

              <div>
                <Label>Email</Label>
                <Input
                  type="email"
                  value={formData.email}
                  onChange={(e) =>
                    setFormData({ ...formData, email: e.target.value })
                  }
                  disabled={!isEditing}
                  data-testid="input-email"
                />
              </div>

              <div>
                <Label>Telefone</Label>
                <Input
                  value={formData.phone}
                  onChange={(e) =>
                    setFormData({ ...formData, phone: e.target.value })
                  }
                  disabled={!isEditing}
                  placeholder="+55 11 99999-9999"
                  data-testid="input-phone"
                />
              </div>

              <div>
                <Label>Status da Conta</Label>
                <div className="mt-2">
                  <Badge variant={user.status === 'approved' ? 'default' : 'secondary'}>
                    {user.status === 'approved' ? 'Aprovado' : user.status}
                  </Badge>
                </div>
              </div>
            </div>
          </Card>

          {/* Condom√≠nio */}
          {condo && (
            <Card className="p-6">
              <div className="flex items-center justify-between mb-4">
                <div className="flex items-center gap-3">
                  <div className="w-12 h-12 rounded-full bg-primary/10 flex items-center justify-center">
                    <Building2 className="w-6 h-6 text-primary" />
                  </div>
                  <div>
                    <h2 className="text-xl font-semibold">Condom√≠nio</h2>
                    <p className="text-sm text-muted-foreground">{condo.name}</p>
                  </div>
                </div>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setShowChangeCondoDialog(true)}
                  data-testid="button-change-condo"
                >
                  <Building2 className="w-4 h-4 mr-2" />
                  Mudar
                </Button>
              </div>

              <div className="space-y-2 text-sm">
                <p className="text-muted-foreground">
                  {condo.address}, {condo.city} - {condo.state}
                </p>
              </div>
            </Card>
          )}

          {/* A√ß√µes */}
          <Card className="p-6">
            <h2 className="text-xl font-semibold mb-4">Configura√ß√µes da Conta</h2>
            <div className="space-y-3">
              <Button
                variant="outline"
                className="w-full justify-start"
                onClick={() => setLocation("/family")}
                data-testid="button-family-account"
              >
                <Users className="w-4 h-4 mr-2" />
                Conta Fam√≠lia
              </Button>
              <Button
                variant="destructive"
                className="w-full justify-start"
                onClick={() => setShowLogoutDialog(true)}
                data-testid="button-logout"
              >
                <LogOut className="w-4 h-4 mr-2" />
                Sair da Conta
              </Button>
            </div>
          </Card>
        </div>
      </main>

      {/* Logout Dialog */}
      <AlertDialog open={showLogoutDialog} onOpenChange={setShowLogoutDialog}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Sair da conta?</AlertDialogTitle>
            <AlertDialogDescription>
              Voc√™ ser√° desconectado e precisar√° fazer login novamente.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel data-testid="button-cancel-logout">
              Cancelar
            </AlertDialogCancel>
            <AlertDialogAction
              onClick={handleLogout}
              data-testid="button-confirm-logout"
            >
              Sair
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>

      {/* Change Condo Dialog */}
      <AlertDialog
        open={showChangeCondoDialog}
        onOpenChange={setShowChangeCondoDialog}
      >
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Mudar de condom√≠nio?</AlertDialogTitle>
            <AlertDialogDescription>
              Voc√™ ser√° redirecionado para selecionar um novo condom√≠nio.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel data-testid="button-cancel-change-condo">
              Cancelar
            </AlertDialogCancel>
            <AlertDialogAction
              onClick={handleChangeCondo}
              data-testid="button-confirm-change-condo"
            >
              Mudar
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
}

