import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { UserPlus, Trash2, Edit, AlertCircle, Users } from "lucide-react";

interface Dependent {
  id: string;
  name: string;
  birthDate: string;
  accountType: string;
  relationship: string;
  status: string;
}

export default function FamilyAccountPage() {
  const { toast } = useToast();
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [editingDependent, setEditingDependent] = useState<Dependent | null>(null);
  const [formData, setFormData] = useState({
    name: "",
    birthDate: "",
    relationship: "",
  });

  const { data: dependents, isLoading } = useQuery<Dependent[]>({
    queryKey: ["/api/family/dependents"],
  });

  const addDependentMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      return await apiRequest("POST", "/api/family/dependents", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/family/dependents"] });
      setIsAddDialogOpen(false);
      setFormData({ name: "", birthDate: "", relationship: "" });
      toast({
        title: "Sucesso",
        description: "Dependente adicionado com sucesso",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Erro",
        description: error.message || "Erro ao adicionar dependente",
        variant: "destructive",
      });
    },
  });

  const updateDependentMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<typeof formData> }) => {
      return await apiRequest("PATCH", `/api/family/dependents/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/family/dependents"] });
      setEditingDependent(null);
      setFormData({ name: "", birthDate: "", relationship: "" });
      toast({
        title: "Sucesso",
        description: "Dependente atualizado com sucesso",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Erro",
        description: error.message || "Erro ao atualizar dependente",
        variant: "destructive",
      });
    },
  });

  const deleteDependentMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest("DELETE", `/api/family/dependents/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/family/dependents"] });
      toast({
        title: "Sucesso",
        description: "Dependente removido com sucesso",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Erro",
        description: error.message || "Erro ao remover dependente",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (editingDependent) {
      updateDependentMutation.mutate({
        id: editingDependent.id,
        data: {
          name: formData.name,
          relationship: formData.relationship,
        },
      });
    } else {
      addDependentMutation.mutate(formData);
    }
  };

  const handleEdit = (dependent: Dependent) => {
    setEditingDependent(dependent);
    setFormData({
      name: dependent.name,
      birthDate: dependent.birthDate || "",
      relationship: dependent.relationship || "",
    });
  };

  const handleDelete = (id: string) => {
    if (confirm("Tem certeza que deseja remover este dependente?")) {
      deleteDependentMutation.mutate(id);
    }
  };

  const calculateAge = (birthDate: string) => {
    if (!birthDate) return 0;
    const today = new Date();
    const birth = new Date(birthDate);
    let age = today.getFullYear() - birth.getFullYear();
    const monthDiff = today.getMonth() - birth.getMonth();
    if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birth.getDate())) {
      age--;
    }
    return age;
  };

  return (
    <div className="container mx-auto p-6 max-w-5xl">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h1 className="text-3xl font-bold flex items-center gap-2">
            <Users className="w-8 h-8" />
            Conta Fam√≠lia
          </h1>
          <p className="text-muted-foreground mt-1">
            Gerencie seus dependentes menores de 18 anos
          </p>
        </div>

        <Dialog open={isAddDialogOpen || !!editingDependent} onOpenChange={(open) => {
          setIsAddDialogOpen(open);
          if (!open) {
            setEditingDependent(null);
            setFormData({ name: "", birthDate: "", relationship: "" });
          }
        }}>
          <DialogTrigger asChild>
            <Button data-testid="button-add-dependent">
              <UserPlus className="w-4 h-4 mr-2" />
              Adicionar Dependente
            </Button>
          </DialogTrigger>
          <DialogContent>
            <form onSubmit={handleSubmit}>
              <DialogHeader>
                <DialogTitle>
                  {editingDependent ? "Editar Dependente" : "Adicionar Dependente"}
                </DialogTitle>
                <DialogDescription>
                  {editingDependent
                    ? "Atualize as informa√ß√µes do dependente"
                    : "Adicione um dependente menor de 18 anos √† sua conta"}
                </DialogDescription>
              </DialogHeader>

              <div className="space-y-4 py-4">
                <div>
                  <Label htmlFor="name">Nome Completo *</Label>
                  <Input
                    id="name"
                    value={formData.name}
                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                    placeholder="Ex: Jo√£o Silva"
                    required
                    data-testid="input-dependent-name"
                  />
                </div>

                {!editingDependent && (
                  <div>
                    <Label htmlFor="birthDate">Data de Nascimento *</Label>
                    <Input
                      id="birthDate"
                      type="date"
                      value={formData.birthDate}
                      onChange={(e) => setFormData({ ...formData, birthDate: e.target.value })}
                      max={new Date().toISOString().split('T')[0]}
                      required
                      data-testid="input-dependent-birthdate"
                    />
                    <p className="text-xs text-muted-foreground mt-1">
                      Dependente deve ter menos de 18 anos
                    </p>
                  </div>
                )}

                <div>
                  <Label htmlFor="relationship">Grau de Parentesco *</Label>
                  <Select
                    value={formData.relationship}
                    onValueChange={(value) => setFormData({ ...formData, relationship: value })}
                    required
                  >
                    <SelectTrigger data-testid="select-relationship">
                      <SelectValue placeholder="Selecione o parentesco" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="filho">Filho</SelectItem>
                      <SelectItem value="filha">Filha</SelectItem>
                      <SelectItem value="sobrinho">Sobrinho</SelectItem>
                      <SelectItem value="sobrinha">Sobrinha</SelectItem>
                      <SelectItem value="dependente">Dependente</SelectItem>
                      <SelectItem value="outro">Outro</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <DialogFooter>
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => {
                    setIsAddDialogOpen(false);
                    setEditingDependent(null);
                    setFormData({ name: "", birthDate: "", relationship: "" });
                  }}
                  data-testid="button-cancel"
                >
                  Cancelar
                </Button>
                <Button
                  type="submit"
                  disabled={addDependentMutation.isPending || updateDependentMutation.isPending}
                  data-testid="button-submit-dependent"
                >
                  {editingDependent ? "Atualizar" : "Adicionar"}
                </Button>
              </DialogFooter>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      <Alert className="mb-6">
        <AlertCircle className="h-4 w-4" />
        <AlertDescription>
          <strong>Conta Fam√≠lia:</strong> Adicione e gerencie dependentes menores de 18 anos.
          Voc√™ ser√° respons√°vel por aprovar as compras realizadas por eles.
        </AlertDescription>
      </Alert>

      {isLoading ? (
        <div className="text-center py-12">
          <p className="text-muted-foreground">Carregando dependentes...</p>
        </div>
      ) : !dependents || dependents.length === 0 ? (
        <Card>
          <CardContent className="flex flex-col items-center justify-center py-12">
            <Users className="w-16 h-16 text-muted-foreground mb-4" />
            <p className="text-xl font-semibold mb-2">Nenhum dependente cadastrado</p>
            <p className="text-muted-foreground text-center mb-6">
              Adicione dependentes menores de 18 anos para que possam usar o app sob sua supervis√£o
            </p>
          </CardContent>
        </Card>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {dependents.map((dependent) => (
            <Card key={dependent.id} data-testid={`card-dependent-${dependent.id}`}>
              <CardHeader>
                <CardTitle className="flex items-center justify-between">
                  <span>{dependent.name}</span>
                  <div className="flex gap-2">
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={() => handleEdit(dependent)}
                      data-testid={`button-edit-${dependent.id}`}
                    >
                      <Edit className="w-4 h-4" />
                    </Button>
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={() => handleDelete(dependent.id)}
                      data-testid={`button-delete-${dependent.id}`}
                    >
                      <Trash2 className="w-4 h-4 text-destructive" />
                    </Button>
                  </div>
                </CardTitle>
                <CardDescription>{dependent.relationship}</CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-2 text-sm">
                  <div className="flex justify-between">
                    <span className="text-muted-foreground">Idade:</span>
                    <span className="font-medium">
                      {calculateAge(dependent.birthDate)} anos
                    </span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-muted-foreground">Status:</span>
                    <span className="font-medium">
                      {dependent.status === "blocked_until_18" ? "Bloqueado (<18)" : dependent.status}
                    </span>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}

client/src/pages/MarketplacePage.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Card, CardHeader, CardTitle, CardDescription, CardContent, CardFooter } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Plus, Edit, Trash2, Filter } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { Badge } from "@/components/ui/badge";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";

type MarketplaceItem = {
  id: string;
  condoId: string;
  userId: string;
  title: string;
  description: string | null;
  images: string[] | null;
  category: string | null;
  type: "sale" | "donation" | "exchange";
  price: string | null;
  block: string | null;
  unit: string | null;
  status: "available" | "sold" | "reserved" | "removed";
  views: number;
  createdAt: Date;
  updatedAt: Date;
};

// Schema de valida√ß√£o (baseado no insertMarketplaceItemSchema do backend)
const marketplaceFormSchema = z
  .object({
    title: z.string().min(1, "T√≠tulo √© obrigat√≥rio").max(200, "T√≠tulo muito longo"),
    description: z.string().max(2000, "Descri√ß√£o muito longa").optional(),
    category: z.string().max(100, "Categoria muito longa").optional(),
    type: z.enum(["sale", "donation", "exchange"], { required_error: "Tipo √© obrigat√≥rio" }),
    price: z.string().optional(),
  })
  .refine(
    (data) => {
      // Se for venda, pre√ßo √© obrigat√≥rio
      if (data.type === "sale") {
        return !!data.price && parseFloat(data.price) > 0;
      }
      return true;
    },
    {
      message: "Pre√ßo √© obrigat√≥rio para vendas",
      path: ["price"],
    }
  );

type MarketplaceFormValues = z.infer<typeof marketplaceFormSchema>;

export default function MarketplacePage() {
  const { toast } = useToast();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingItem, setEditingItem] = useState<MarketplaceItem | null>(null);
  const [filterType, setFilterType] = useState<string>("all");
  const [filterCategory, setFilterCategory] = useState<string>("all");

  const form = useForm<MarketplaceFormValues>({
    resolver: zodResolver(marketplaceFormSchema),
    defaultValues: {
      title: "",
      description: "",
      category: "",
      type: "sale",
      price: "",
    },
  });

  const { data: items, isLoading } = useQuery<MarketplaceItem[]>({
    queryKey: ["/api/marketplace"],
  });

  const addItemMutation = useMutation({
    mutationFn: async (data: MarketplaceFormValues) => {
      return await apiRequest("POST", "/api/marketplace", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/marketplace"] });
      setIsDialogOpen(false);
      form.reset();
      toast({
        title: "Sucesso",
        description: "Item adicionado ao marketplace",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Erro",
        description: error.message || "Erro ao adicionar item",
        variant: "destructive",
      });
    },
  });

  const updateItemMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<MarketplaceFormValues> }) => {
      return await apiRequest("PATCH", `/api/marketplace/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/marketplace"] });
      setEditingItem(null);
      setIsDialogOpen(false);
      form.reset();
      toast({
        title: "Sucesso",
        description: "Item atualizado",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Erro",
        description: error.message || "Erro ao atualizar item",
        variant: "destructive",
      });
    },
  });

  const deleteItemMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest("DELETE", `/api/marketplace/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/marketplace"] });
      toast({
        title: "Sucesso",
        description: "Item removido",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Erro",
        description: error.message || "Erro ao remover item",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: MarketplaceFormValues) => {
    if (editingItem) {
      updateItemMutation.mutate({
        id: editingItem.id,
        data,
      });
    } else {
      addItemMutation.mutate(data);
    }
  };

  const handleEdit = (item: MarketplaceItem) => {
    setEditingItem(item);
    form.reset({
      title: item.title,
      description: item.description || "",
      category: item.category || "",
      type: item.type,
      price: item.price || "",
    });
    setIsDialogOpen(true);
  };

  const handleDelete = (id: string) => {
    if (confirm("Tem certeza que deseja remover este item?")) {
      deleteItemMutation.mutate(id);
    }
  };

  const handleAddNew = () => {
    setEditingItem(null);
    form.reset({
      title: "",
      description: "",
      category: "",
      type: "sale",
      price: "",
    });
    setIsDialogOpen(true);
  };

  const filteredItems = items?.filter((item) => {
    const typeMatch = filterType === "all" || item.type === filterType;
    const categoryMatch = filterCategory === "all" || item.category === filterCategory;
    return typeMatch && categoryMatch;
  });

  const categories = Array.from(new Set(items?.map((i) => i.category).filter(Boolean) || []));

  const getTypeLabel = (type: string) => {
    switch (type) {
      case "sale":
        return "Venda";
      case "donation":
        return "Doa√ß√£o";
      case "exchange":
        return "Troca";
      default:
        return type;
    }
  };

  const getStatusLabel = (status: string) => {
    switch (status) {
      case "available":
        return "Dispon√≠vel";
      case "sold":
        return "Vendido";
      case "reserved":
        return "Reservado";
      case "removed":
        return "Removido";
      default:
        return status;
    }
  };

  const watchType = form.watch("type");

  return (
    <div className="container mx-auto p-4 max-w-6xl">
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-3xl font-bold">Marketplace</h1>
          <p className="text-muted-foreground">Venda, doe ou troque itens com seus vizinhos</p>
        </div>
        <Button onClick={handleAddNew} data-testid="button-add-item">
          <Plus className="w-4 h-4 mr-2" />
          Adicionar Item
        </Button>
      </div>

      {/* Filtros */}
      <Card className="mb-6">
        <CardHeader>
          <CardTitle className="flex items-center">
            <Filter className="w-5 h-5 mr-2" />
            Filtros
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label htmlFor="filter-type" className="text-sm font-medium">
                Tipo
              </label>
              <Select value={filterType} onValueChange={setFilterType}>
                <SelectTrigger id="filter-type" data-testid="filter-type">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Todos</SelectItem>
                  <SelectItem value="sale">Vendas</SelectItem>
                  <SelectItem value="donation">Doa√ß√µes</SelectItem>
                  <SelectItem value="exchange">Trocas</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div>
              <label htmlFor="filter-category" className="text-sm font-medium">
                Categoria
              </label>
              <Select value={filterCategory} onValueChange={setFilterCategory}>
                <SelectTrigger id="filter-category" data-testid="filter-category">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Todas</SelectItem>
                  {categories.map((cat) => (
                    <SelectItem key={cat} value={cat as string}>
                      {cat}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Lista de Itens */}
      {isLoading ? (
        <div className="text-center py-8">Carregando...</div>
      ) : filteredItems && filteredItems.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {filteredItems.map((item) => (
            <Card key={item.id} data-testid={`card-item-${item.id}`} className="hover-elevate">
              <CardHeader>
                <div className="flex justify-between items-start">
                  <CardTitle className="text-lg">{item.title}</CardTitle>
                  <div className="flex gap-1">
                    <Badge variant={item.type === "sale" ? "default" : item.type === "donation" ? "secondary" : "outline"}>
                      {getTypeLabel(item.type)}
                    </Badge>
                  </div>
                </div>
                <CardDescription>{item.description || "Sem descri√ß√£o"}</CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-2 text-sm">
                  {item.category && (
                    <div>
                      <span className="font-semibold">Categoria:</span> {item.category}
                    </div>
                  )}
                  {item.type === "sale" && item.price && (
                    <div className="text-lg font-bold text-primary">
                      R$ {parseFloat(item.price).toFixed(2)}
                    </div>
                  )}
                  {item.block && item.unit && (
                    <div>
                      <span className="font-semibold">Localiza√ß√£o:</span> Bloco {item.block}, Apto {item.unit}
                    </div>
                  )}
                  <div>
                    <Badge variant={item.status === "available" ? "default" : "secondary"}>
                      {getStatusLabel(item.status)}
                    </Badge>
                  </div>
                </div>
              </CardContent>
              <CardFooter className="flex gap-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => handleEdit(item)}
                  data-testid={`button-edit-${item.id}`}
                >
                  <Edit className="w-4 h-4 mr-1" />
                  Editar
                </Button>
                <Button
                  variant="destructive"
                  size="sm"
                  onClick={() => handleDelete(item.id)}
                  data-testid={`button-delete-${item.id}`}
                >
                  <Trash2 className="w-4 h-4 mr-1" />
                  Remover
                </Button>
              </CardFooter>
            </Card>
          ))}
        </div>
      ) : (
        <Card>
          <CardContent className="py-8 text-center text-muted-foreground">
            Nenhum item encontrado no marketplace
          </CardContent>
        </Card>
      )}

      {/* Dialog para Adicionar/Editar Item */}
      <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>{editingItem ? "Editar Item" : "Adicionar Item"}</DialogTitle>
          </DialogHeader>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="title"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>T√≠tulo *</FormLabel>
                    <FormControl>
                      <Input {...field} data-testid="input-title" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="description"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Descri√ß√£o</FormLabel>
                    <FormControl>
                      <Textarea {...field} data-testid="input-description" rows={3} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="type"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Tipo *</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-type">
                          <SelectValue />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="sale">Venda</SelectItem>
                        <SelectItem value="donation">Doa√ß√£o</SelectItem>
                        <SelectItem value="exchange">Troca</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="category"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Categoria</FormLabel>
                    <FormControl>
                      <Input {...field} data-testid="input-category" placeholder="Ex: Roupas, M√≥veis, Eletr√¥nicos..." />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {watchType === "sale" && (
                <FormField
                  control={form.control}
                  name="price"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Pre√ßo (R$) *</FormLabel>
                      <FormControl>
                        <Input {...field} data-testid="input-price" type="number" step="0.01" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              )}

              <DialogFooter className="mt-6">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => {
                    setIsDialogOpen(false);
                    form.reset();
                  }}
                >
                  Cancelar
                </Button>
                <Button type="submit" data-testid="button-submit-item" disabled={form.formState.isSubmitting}>
                  {editingItem ? "Atualizar" : "Adicionar"}
                </Button>
              </DialogFooter>
            </form>
          </Form>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/LoginPage.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card } from "@/components/ui/card";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { AlertCircle, LogIn, ArrowLeft } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

export default function LoginPage() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      const response = await fetch("/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || "Erro ao fazer login");
      }

      // Salvar token e dados do usu√°rio
      localStorage.setItem("token", data.token);
      localStorage.setItem("user", JSON.stringify(data.user));

      toast({
        title: "Login realizado",
        description: "Bem-vindo de volta!",
      });

      // Redirecionar para home
      window.location.href = "/";
    } catch (err: any) {
      setError(err.message);
      toast({
        title: "Erro no login",
        description: err.message,
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-background flex flex-col items-center justify-center p-4">
